import React, { useState } from 'react'
import {Link,useNavigate} from 'react-router-dom'
import Badge from 'react-bootstrap/Badge';
import Modal from '../Modal';
import Cart from '../screens/Cart';
import { useCart } from './ContextReducer';

function Navbar() {
  const [cartView,setCartView]=useState(false)
  let data= useCart();
  const navigate=useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token')
    navigate("/login")
}
  return (
    <div>
    <nav className="navbar navbar-expand-lg navbar-dark bg-success">
    <div className="container-fluid">
      <Link className="navbar-brand fs-1 fst-italic" to="/">DairyDelights.com</Link>
      <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav me-auto mb-2">

          <li className="nav-item">
            <Link className="nav-link active fs-5" aria-current="page" href="/">Home</Link>
          </li>
          {(localStorage.getItem("authToken")) ?
              <li className="nav-item">
                <Link className="nav-link fs-5 active" aria-current="page" to="/" >My Orders</Link>  {/* index.css - nav-link color white */}
              </li> : ""}
        </ul>

        <div className='d-flex'>
            <Link className="btn bg-white text-success mx-1" href="/login">Login</Link>
            <Link className="btn bg-white text-success mx-1" href="/creatuser">Signup</Link>
        </div>
        <div>
          <div className='btn bg-white text-success mx-2' onClick={()=>{setCartView(true)}}>
           My Cart {" "}
             <Badge pill bg='danger'> {data.length}</Badge>
          </div>
          {cartView? <Modal onClose={()=>setCartView(false)}><Cart/></Modal>: null}
          <div className='btn bg-white text-danger mx-2' onClick={handleLogout}>
           Logout
          </div>
        </div>
      </div>
    </div>
  </nav>
 </div>
  )
}

export default Navbar
