import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Card from '../components/Card';
import Carousel from '../components/Carousel';

function Home() {
  const [milkCat, setMilkCat] = useState([]);
  const [milkItem, setMilkItem] = useState([]);

  const loadData = async () => {
    try {
      let response = await fetch("http://localhost:5000/api/milkData", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        }
      });
      response = await response.json();

      setMilkItem(response[0]);
      setMilkCat(response[1]);
    } catch (error) {
      console.error("Error loading data:", error);
    }
  }

  useEffect(() => {
    loadData()
  }, [])

  return (
  <div>
    <div><Navbar /></div>
    <div><Carousel /></div>
    <div className='container'>
      {milkCat.length !== 0 ? (
        milkCat.map((data) => {
          return (
            <div className='row mb-3' key={data.id}>
              <div className='fs-3 m-3'>
                {data.CategoryName}
              </div>
              <hr id="hr-success" style={{ height: "4px", backgroundImage: "-webkit-linear-gradient(left,rgb(0, 255, 137),rgb(0, 0, 0))" }} />
              {milkItem.length !== 0 ? (
                milkItem
                  .filter((items) => (items.CategoryName === data.CategoryName))
                  .map(filterItems => {
                    return (
                      <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                        {console.log(filterItems.url)}
                        <Card foodName={filterItems.name} item={filterItems} options={filterItems.options[0]} ImgSrc={filterItems.img} ></Card>
                      </div>
                    )
                  })
              ) : (
                <div> No Such Data </div>
              )}
            </div>
          )
        })
      ) : (
        ""
      )}
      <Card />
    </div>
    <div><Footer /></div>
  </div>
);

}
export default Home;
