import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Card from '../components/Card';
import Carousel from '../components/Carousel';

function Home() {
  const [milkCat, setMilkCat] = useState([]);
  const [milkItem, setMilkItem] = useState([]);

  const loadData = async () => {
    try {
      let response = await fetch("http://localhost:5000/api/milkData", {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        }
      });
      response = await response.json();

      setMilkItem(response[0]);
      setMilkCat(response[1]);
    } catch (error) {
      console.error("Error loading data:", error);
    }
  }

  useEffect(() => {
    loadData()
  }, [])

  return (
    <div>
      <div><Navbar /></div>
      <div><Carousel /></div>
      <div className='container'>
        {milkCat.length > 0 ? (
          milkCat.map((data) => {
            return (
              <div>
              <div key={data.id} className='fs-3 m-3'>
                  {data.CategoryName}
              </div>
              <hr/>
              {milkItem!==[]
                ? 
                milkItem.filter((item)=>item.CategoryName === data.CategoryName).map(filterItem=>{
                return(
                  <div key={filterItem,_id}>
                    <Card></Card>
                  </div>
                )
              })
                :<div>No such data found</div>}
              </div>
            )
          })
        ) : (
          ""
        )}
        <Card />
      </div>
      <div><Footer /></div>
    </div>
  )
}

export default Home;
