import React, { useState, useRef } from 'react';
import { useCart, useDispatchCart } from './ContextReducer';

function Card(props) {
  let dispatch = useDispatchCart();
  let data = useCart();
  const priceRef = useRef();
  let options = props.options;
  let priceOptions = Object.keys(options);
  const [qty, setQty] = useState(1);
  const [size, setSize] = useState('');
  let foodItem = props.item;

  const handleAddToCart = async () => {
    let food = [];
    for (const item of data) {
      if (item.id === foodItem._id) {
        food = item;
        break;
      }
    }
    if (food.length > 0) {
      if (food.size === size) {
        let finalPrice = qty * parseInt(options[size]);
        await dispatch({ type: 'UPDATE', id: foodItem._id, price: finalPrice, qty: qty });
        return;
      } else if (food.size !== size) {
        let finalPrice = qty * parseInt(options[size]);
        await dispatch({
          type: 'ADD',
          id: foodItem._id,
          name: foodItem.name,
          price: finalPrice,
          qty: qty,
          size: size,
          img: props.ImgSrc,
        });
        console.log('Size different so simply ADD one more to the list');
        return;
      }
      return;
    }
    let finalPrice = qty * parseInt(options[size]);
    await dispatch({
      type: 'ADD',
      id: foodItem._id,
      name: foodItem.name,
      price: finalPrice,
      qty: qty,
      size: size,
    });
  };
  let finalPrice = qty * parseInt(options[size]);
  return (
    <div>
      <div className="card mt-3" style={{ width: '16rem', maxHeight: '360px' }}>
        <img src={props.foodItem.img} className="card-img-top" alt="..." style={{ height: '180px', objectFit: 'fill' }} />
        <div className="card-body">
          <h5 className="card-title">{props.foodItem.name}</h5>
          <div className="container w-100">
            <select className="m-2 h-100 w-100 bg success rounded" onChange={(e) => setQty(e.target.value)}>
              {Array.from(Array(6), (e, i) => {
                return <option key={i + 1} value={i + 1}>{i + 1}</option>;
              })}
            </select>
            <select className="m-2 h-100 w-100 bg success rounded" ref={priceRef} onChange={(e) => setSize(e.target.value)}>
              {priceOptions.map((data) => {
                return <option key={data} value={data}>{data}</option>;
              })}
            </select>
            <div className="d-inline h-100 fs-5">
              â‚¹{finalPrice}/-
            </div>
          </div>
          <hr />
          <button className={'btn btn-success justify-center ms-2'} onClick={handleAddToCart}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default Card;
